import React, { Component } from 'react'
import AuthService from '../../service/auth.service'
import Container from 'react-bootstrap/Container'

// import { Link } from 'react-router-dom'

class Profile extends Component {

    constructor(props) {
        super(props)
        this.authService = new AuthService()
        this.state = {
            user: null,
            isLoaded: false,
            posts: []
        }
    }


    setUser = (user) => {
        this.setState({ user })
    }


    fetchUser = () => {
        if (this.state.user === null) {

            this.authService.isLoggedIn()
                .then(
                    (response) => {
                        this.setUser(response.data)
                    },
                    (error) => {
                        this.setUser(false)
                    }
                )
                .catch(() => {
                    this.setUser(false)
                })
        }
    }

    componentDidMount() {
        this.fetchUser()
        fetch('http://localhost:3000/api/post')
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        isLoaded: true,
                        posts: result
                    });
                },
                // error handler
                (error) => {
                    this.setState({
                        isLoaded: true,
                        error
                    });
                }
            )
    }

    render() {
        if (this.state.user) {
            return (
                <>
                    <Container as="section" className="profile-section">
                        <div><h1>HOLA, {this.state.user.username}</h1>
                            {this.state.posts.map(post => <div>{post.title}</div>)}
                        </div>
                    </Container>
                </>
            )
        } else {
            return <p>Cargando...</p>
        }

    }
}

export default Profile
