import React, { Component } from 'react'
import Container from 'react-bootstrap/Container'
import PostService from '../../../service/post.service'
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'
import { Link } from 'react-router-dom'
import Review from '../Review/Review'

class UserPost extends Component {
    constructor(props) {
        super(props)
        this.state = {
            postInfo: {}
        }
        this.postService = new PostService()
    }

    displayThePost = () => {
        const id = this.props.match.params.id
        this.postService.getUserPost(id)
            .then(response => this.setState(response.data))
            .catch(err => console.log(err))
    }

    handleNewReview() {
        this.displayThePost()
            .then(response => this.setState({
                restaurantInfo: response.data
            }))
            .catch(err => console.log(err))
    }



    displayReviews = () => {
        return this.state.postInfo.myReviews.map(review => <Review key={review._id} newReviewAdded={() => this.handleNewReview()}  {...review} />)
    }

    // averageRating = () => {
    //     return this.state.postInfo.myReviews.reduce((acc, cu) => {
    //         return +acc + cu.rating
    //     }, 0) / this.state.postInfo.myReviews.length
    // }


    componentDidMount = () => {
        this.displayThePost()
    }

    render() {
        return (
            <Container as="section" className="post-details">
                <h1>{this.state.title}</h1>
                <Row>
                    <Col md={{ span: 4, offset: 1 }}>
                        <p>{this.state.content}</p>
                        <ul>
                            <li>Genre: {this.state.genre}</li>
                            <li>Typology: {this.state.typology}</li>
                        </ul>
                    </Col>
                    <Col md={6}>
                        <img style={{ width: '200px' }} src={this.state.cover} alt={this.state.title} />
                        <h3>Comments</h3>
                        <p>{this.state.comments.content}</p>
                    </Col>
                </Row>
                <Row>
                    <Col md={{ span: 8, offset: 1 }}>
                        <h5>{this.state.postInfo.myReviews}</h5>
                    </Col>

                    <Col md={{ span: 8, offset: 1 }}>
                        <hr />
                        <p>Comments</p>
                        <hr />

                    </Col>
                    {this.state.postInfo.myReviews && this.displayReviews()}
                </Row>

                <Link to="/explore" >Go back</Link>
            </Container>

        )
    }
}


export default UserPost
