import axios from 'axios'

export default class services {
    constructor() {
        this.service = axios.create({
            baseURL: 'http://localhost:5000/api',
            withCredentials: true
        })
    }

    signup = ({ username, email, password, favoriteGenre }) => this.service.post('/auth/signup', { username, email, password, favoriteGenre })
    login = ({ username, password }) => this.service.post('/auth/login', { username, password })
    logout = () => this.service.post('/auth/logout')
    isLoggedIn = () => this.service.get('/auth/loggedin')
    userProfile = () => this.service.get('/auth/:username')
}
